export default function(tweet){
    return dispatch=>{
        debugger
        fetch('http://localhost:3001/tweets', {
            method: 'POST',
            credentials: 'include',
            body:{tweet}
        })
        .then(res=>res.json())
        .then(tweet=>{
            dispatch({type: 'ADD_TWEET',preload: tweet})
        })
    }
}
